mapper.select:
SELECT r.id, r.title, r.`right`, r.description, r.size, r.extension, r.cover_image, r.user_id, r.price, r.download_count, r.collect_count, r.like_count, r.publish_time, r.update_time, u.username, u.avatar, -- 是否点赞：true=已点赞，false=未点赞 EXISTS( SELECT 1 FROM user_likes ul WHERE ul.target_id = r.id AND ul.target_type = 1 AND ul.user_id = #{dto.userId} AND ul.deleted = 0 ) AS isLiked, -- 是否收藏：true=已收藏，false=未收藏 EXISTS( SELECT 1 FROM user_collections uf WHERE uf.target_id = r.id AND uf.target_type = 1 AND uf.user_id = #{dto.userId} AND uf.deleted = 0 ) AS isCollected, EXISTS( SELECT 1 FROM user_purchases up WHERE up.target_id = r.id AND up.target_type = 1 AND up.user_id = #{dto.userId} AND up.deleted = 0 ) AS isPurchased FROM resources r LEFT JOIN users u ON r.user_id = u.id AND u.deleted = 0 WHERE r.deleted = 0 AND r.status = 3
AND r.`right` = #{dto.right}
AND r.subject_id = #{dto.subjectId}
AND EXISTS( SELECT 1 FROM resource_software rs WHERE rs.resource_id = r.id AND rs.deleted = 0 AND rs.software_id IN
#{tool}
)
AND EXISTS( SELECT 1 FROM resource_category rc WHERE rc.resource_id = r.id AND rc.deleted = 0 AND rc.category_id IN
#{category}
)
AND ( r.title LIKE CONCAT('%', #{dto.search}, '%') OR r.description LIKE CONCAT('%', #{dto.search}, '%') OR r.details LIKE CONCAT('%', #{dto.search}, '%') )
GROUP BY
r.id, r.title, r.`right`, r.description, r.size, r.extension, r.cover_image, r.user_id, r.price, r.download_count, r.collect_count, r.like_count, r.publish_time, r.update_time, u.username, u.avatar
ORDER BY r.publish_time DESC
ORDER BY r.collect_count DESC
ORDER BY r.download_count DESC
ORDER BY r.like_count DESC
ORDER BY (r.download_count * 0.3 + r.collect_count * 0.2 + r.like_count * 0.5) DESC
ORDER BY r.publish_time DESC
